function burgerMenu(){const e=document.querySelector(".burger"),c=document.querySelector(".burger__menu"),t=document.querySelector("body"),s=document.querySelector(".header__buttons");e.addEventListener("click",(()=>{c.classList.contains("active")?(c.classList.remove("active"),e.classList.remove("active"),t.classList.remove("locked"),s.classList.remove("active")):(c.classList.add("active"),e.classList.add("active"),t.classList.add("locked"),s.classList.add("active"))})),window.addEventListener("resize",(()=>{window.innerWidth>767.98&&(c.classList.remove("active"),e.classList.remove("active"),t.classList.remove("locked"),s.classList.remove("active"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function accardeon(){document.querySelector(".acardeon");document.querySelectorAll(".acardeon__title").forEach(((e,c)=>{e.addEventListener("click",(c=>{c.preventDefault();const t=e.closest(".acardeon__item").querySelector(".acardeon__content");e.classList.contains("active")?(e.classList.remove("active"),t.classList.remove("active")):(e.classList.add("active"),t.classList.add("active"))}))}))}burgerMenu(),window.addEventListener("scroll",fixedNav),accardeon();