function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".burger__menu"),t=document.querySelector("body"),c=document.querySelector(".header__buttons");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active"),t.classList.remove("locked"),c.classList.remove("active")):(s.classList.add("active"),e.classList.add("active"),t.classList.add("locked"),c.classList.add("active"))})),window.addEventListener("resize",(()=>{window.innerWidth>767.98&&(s.classList.remove("active"),e.classList.remove("active"),t.classList.remove("locked"),c.classList.remove("active"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);